function HomeScreen({ navigation }) {
  const levels = Array.from({ length: 50 }, (_, index) => index + 1);

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Welcome to My Game</Text>
      <ScrollView style={styles.scrollView} horizontal={true}>
        {levels.map((level) => (
          <View key={level} style={styles.buttonContainer}>
            <Button title={`Level ${level}`} onPress={() => navigation.navigate('Level', { level })} />
          </View>
        ))}
      </ScrollView>
    </View>
  );
}

function LevelButton({ level, navigation }) {
  const goToLevel = () => {
    navigation.navigate('Level', { level });
  };

  return (
    <View style={styles.buttonContainer}>
      <Button title={`Level ${level}`} onPress={goToLevel} />
    </View>
  );
}

function LevelScreen({ route, navigation }) {
  const { level } = route.params;

  const goBackToHome = () => {
    navigation.goBack();
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Level {level}</Text>
      <Button title="Go Back" onPress={goBackToHome} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    fontSize: 24,
    marginBottom: 20,
  },
  scrollView: {
    flex: 1, // Ensure the ScrollView takes up all available space
    width: '100%', // Full width
  },
  buttonContainer: {
    width: 200,
    margin: 10,
  },
});
