import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { useNavigation } from '@react-navigation/native';
import CustomButton from './components/CustomButton';

import HomeScreen from './pages/HomeScreen'; // Import HomeScreen component
import LevelScreen from './pages/LevelScreen'; // Import Level component
import LoginScreen from './pages/LoginScreen';
import RegisterScreen from './pages/RegisterScreen';

const Stack = createStackNavigator();

export default function App() {
  const navigation = useNavigation();

  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Home">
        {/* <Stack.Screen name="Home" component={HomeScreen}/> */}
        {/* <Stack.Screen
          name="Home"
          component={HomeScreen}
          options={{ headerTitle: (props) => 
          <CustomButton
            title={`Login`}
            color='#4287f5'
            height={50}
            width={100}
            onPress={() => navigation.navigate('Login')}
          /> }}
        /> */}
        <Stack.Screen
          name="Home"
          component={HomeScreen}
          options={({route, navigation}) => ({ 
            title: false,
            headerTitle: false,
            headerBackTitleVisible: false,
            headerTransparent: true,
            headerShadowVisible: false,
            headerRight: () => (
                <TouchableOpacity onPress={() => navigation.navigate('CartDetails')}>
                    <CartIcon />
                </TouchableOpacity>
            ),
            headerBackImage: () => (

                    <View style={{backgroundColor: '#ffffff', borderRadius: '50%', marginLeft: 15, marginBottom: 5}}>
                        <Entypo name="chevron-small-left" size={30} color="#37BD6B"/>
                    </View>
            )
        })}  
        />

        <Stack.Screen name="Level" component={LevelScreen} />
        <Stack.Screen name="Login" component={LoginScreen} />
        <Stack.Screen name="Register" component={RegisterScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}
