const exec = require('child_process').exec;

// Execute the Java code in the textfield.

async function executeJavaCode(javaCode) {
  const output = await exec(`java -cp . HelloWorld.java`, {
    input: javaCode,
  });

  return output;
}

module.exports = executeJavaCode;
