import React, { useState } from 'react';
import { View, Text, StyleSheet, ScrollView } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { CodeEditor, CodeEditorLanguage } from 'react-native-code-editor'; // Import CodeEditor components

const Stack = createStackNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen name="Home" component={HomeScreen} options={{ headerShown: false }} />
        <Stack.Screen name="Level" component={LevelScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

function HomeScreen({ navigation }) {
  const levels = Array.from({ length: 50 }, (_, index) => index + 1);

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Welcome to My Game</Text>
      <ScrollView style={styles.scrollView}>
        <View style={styles.row}>
          {levels.map((level) => (
            <View key={level} style={styles.buttonContainer}>
              <CustomButton
                title={`Level ${level}`}
                onPress={() => navigation.navigate('Level', { level })}
              />
            </View>
          ))}
        </View>
      </ScrollView>
    </View>
  );
}

function LevelScreen({ route, navigation }) {
  const { level } = route.params;
  const goBackToHome = () => {
    navigation.goBack();
  };

  // Example code content
  const [code, setCode] = useState(`function myFunction() {
  console.log('Hello, World!');
}`);

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Level {level}</Text>
      {/* Code Editor */}
      <CodeEditor
        code={code}
        onChange={(newCode) => setCode(newCode)}
        language={CodeEditorLanguage.JavaScript}
        style={styles.codeEditor}
      />
      <CustomButton title="Go Back" onPress={goBackToHome} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    fontSize: 24,
    marginBottom: 20,
  },
  scrollView: {
    flex: 1,
    width: '100%',
  },
  row: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
  },
  buttonContainer: {
    marginHorizontal: 10,
    marginVertical: 10,
  },
  codeEditor: {
    width: '100%', // Set the code editor width
    height: 200,   // Set the code editor height
  },
});
