function LevelButton({ level }) {
  const goToLevel = () => {
    navigation.navigate('Level', { level });
  };  

  return (
    <View style={styles.buttonContainer}>
      <Button title={`Level ${level}`} onPress={goToLevel} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    fontSize: 24,
    marginBottom: 20,
  },
  buttonContainer: {
    marginVertical: 10,
  },
});
