import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, Button, StyleSheet, ScrollView } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import RNFS from 'react-native-fs';

const Stack = createStackNavigator();

export default function App() {
  const [levels, setLevels] = useState([]);

  useEffect(() => {
    // Read the files in the 'level_data' folder to determine the number of levels
    const levelDataFolder = RNFS.DocumentDirectoryPath + '/level_data';

    RNFS.readDir(levelDataFolder)
      .then((files) => {
        // Filter out non-JavaScript files (e.g., directories) and get the count
        const levelCount = files.filter((file) => file.isFile() && file.name.endsWith('.js')).length;

        // Generate an array of level numbers from 1 to levelCount
        const levelNumbers = Array.from({ length: levelCount }, (_, index) => index + 1);

        setLevels(levelNumbers);
      })
      .catch((error) => {
        console.error('Error reading level data:', error);
      });
  }, []);

  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen name="Home" component={HomeScreen} options={{ headerShown: false }} />
        <Stack.Screen name="Level" component={LevelScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

function HomeScreen({ navigation }) {
  return (
    <View style={styles.container}>
      <Text style={styles.header}>Welcome to My Game</Text>
      <ScrollView style={styles.scrollView}>
        <View style={styles.row}>
          {levels.map((level) => (
            <View key={level} style={styles.buttonContainer}>
              <CustomButton
                title={`Level ${level}`}
                onPress={() => navigation.navigate('Level', { level })}
              />
            </View>
          ))}
        </View>
      </ScrollView>
    </View>
  );
}

function LevelScreen({ route, navigation }) {
  const { level } = route.params;
  const goBackToHome = () => {
    navigation.goBack();
  };

  // Dynamically import the code for the selected level
  const levelCode = require(`./level_data/level${level}.js`).default;

  const [code, setCode] = useState(levelCode);

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Level {level}</Text>
      <TextInput
        multiline
        style={styles.codeEditor}
        value={code}
        onChangeText={(newCode) => setCode(newCode)}
      />
      <CustomButton title="Go Back" onPress={goBackToHome} />
    </View>
  );
}

const styles = StyleSheet.create({
  // ... your existing styles ...
});

function CustomButton({ title, onPress }) {
  return <Button title={title} onPress={onPress} />;
}
