import { StyleSheet } from 'react-native';

export const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        fontSize: 30,
      },
      header: {
        fontSize: 40,
        marginBottom: 20,
      },
      instruction: {
        fontSize: 30,
        marginBottom: 20,
      },
      scrollView: {
        flex: 1,
        width: '100%',
      },
      row: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        justifyContent: 'center',
      },
      buttonContainer: {
        marginHorizontal: 10,
        marginVertical: 10,
      },
      codeEditor: {
        width: '50%',
        height: 500,
        borderWidth: 1,
        borderColor: '#ccc',
        padding: 10,
        fontSize: 16,
        backgroundColor: '#282c34',
        color: 'white',
      },
      passwordInputContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        position: 'relative', // Added for proper positioning of the eye icon
    },
    passwordInput: {
        flex: 1,
    },
    toggleIconContainer: {
        position: 'absolute',
        right: 10, // Adjust the value based on your preference
    },
    checkboxContainer: {
        width: 17,
        height: 17,
        borderWidth: 1,
        borderColor: 'white',
        borderRadius: 3,
        justifyContent: 'center',
        alignItems: 'center',
    },
    checkbox: {
        width: 8,
        height: 8,
        backgroundColor: 'white',
        borderRadius: 2,
    },
    backgroundImage: {
        flex: 1,
        resizeMode: 'cover',
        justifyContent: 'center',
    },
    scrollViewContent: {
        flexGrow: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
    title: {
        fontSize: 36, // Augmentez la taille du texte
        fontWeight: 'bold',
        marginBottom: 20,
        color: '#fff', // Couleur du texte
        textShadowColor: '#000',
        textShadowOffset: { width: 2, height: 2 },
        textShadowRadius: 5,
        marginTop:70,
    },
    inputContainer: {
        width: 300,
        alignItems: 'flex-start', // Alignez les éléments à gauche
    },
    inputLabel: {
        marginLeft: 5, // Ajoutez une marge à gauche pour déplacer le inputContainer vers la droite
        paddingTop: 10,
        color: 'white',
        fontSize: 17,
        marginBottom: 5,
    },
    input: {
        backgroundColor: '#fff',
        paddingHorizontal: 15,
        paddingVertical: 10,
        borderRadius: 15,
        marginTop: 5,
        borderWidth: 1,
        borderColor: '#ccc',
        width: '100%',


    },
    buttonContainer: {
        width: 220,
        justifyContent: 'center',
        alignItems: 'center',
        marginTop: 40,
    },
    button: {
        backgroundColor: '#248ad9',
        width: '100%',
        padding: 15,
        borderRadius: 20,
        alignItems: 'center',
        marginTop: 20,
    },
    buttonOutline: {
        backgroundColor: '#fff',
        borderColor: '#248ad9',
        borderWidth: 2,
        borderRadius: 20,
        marginTop: 10,
    },
    buttonText: {
        color: 'white',
        fontWeight: '700',
        fontSize: 16,
    },
    buttonOutlineText: {
        color: '#248ad9',
        fontWeight: '700',
        fontSize: 16,
    },
    errorText: {
        color: 'red',
        marginTop: 10,
    },
});
