// HomeScreen.js

import React from 'react';
import { View, Text, Button, ScrollView } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { styles } from './styles'; // Import the styles

function HomeScreen() {
  const navigation = useNavigation();

  const goToLevel1Screen = () => {
    navigation.navigate('Level1'); // Navigate to Level 1
  };

  const levels = Array.from({ length: 1 }, (_, index) => index + 1);
  return (
    <View style={styles.container}>
    <Text style={styles.header}>Welcome to My Game</Text>
    <ScrollView style={styles.scrollView}>
      <View style={styles.row}>
        {levels.map((level) => (
          <View key={level} style={styles.buttonContainer}>
            <CustomButton
              title={`Level ${level}`}
              onPress={() => navigation.navigate('Level1')}
            />
          </View>
        ))}
      </View>
    </ScrollView>
  </View>
  );
}

function CustomButton({ title, onPress }) {
  return (
    <Button title={title} onPress={onPress} />
  );
}

export default HomeScreen;


